import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BookPage() {
  const [books, setBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedBook, setSelectedBook] = useState(null);
  const [title, setTitle, setTitleCreate, setTitleUpdate] = useState('');


  const [author, setAuthor] = useState('');
    const [description, setDescription, setDescriptionCreate, setDescriptionUpdate] = useState('');

  useEffect(() => {
    async function fetchBooks() {
      try {
        const response = await axios.get('http://localhost:8000/books/');
        setBooks(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    }
    fetchBooks();
  }, []);

  const handleDelete = async (bookId) => {
    try {
        const token = localStorage.getItem('access');
      await axios.delete(`http://localhost:8000/books/${bookId}/`, {
        headers: {
            Authorization: `Bearer ${token}`,
        },
        });
      setBooks((prevBooks) => prevBooks.filter((book) => book.id !== bookId));
      setSelectedBook(null);
    } catch (error) {
      console.error(error);
    }
  };

  const handleView = async (bookId) => {
    try {
        const token = localStorage.getItem('access');
      const response = await axios.get(`http://localhost:8000/books/${bookId}/`,
      {
        headers: {
            Authorization: `Bearer ${token}`,
            },
            });
      setSelectedBook(response.data);
      setTitle(response.data.title);
      setAuthor(response.data.author);
      setDescription(response.data.description);
    } catch (error) {
      console.error(error);
    }
  };

  const handleUpdate = async (event) => {
    event.preventDefault();
    try {
        const token = localStorage.getItem('access');
      await axios.put(`http://localhost:8000/books/${selectedBook.id}/`, { title, description },
      {
        headers: {
            Authorization: `Bearer ${token}`,
            },
            });
      const updatedBook = { ...selectedBook, title, description };
      setBooks((prevBooks) =>
        prevBooks.map((book) => (book.id === updatedBook.id ? updatedBook : book))
      );
      setSelectedBook(updatedBook);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCreate = async (event) => {
    event.preventDefault();
    try {
        const token = localStorage.getItem('access');
      const response = await axios.post('http://localhost:8000/books/', { title, description },
      {
        headers: {

            Authorization: `Bearer ${token}`,
            },
            });
      const newBook = response.data;
      setBooks((prevBooks) => [...prevBooks, newBook]);
      setSelectedBook(newBook);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCancel = () => {
    setSelectedBook(null);
    setTitle('');

    setTitleCreate('');
    setTitleUpdate('');
    s
    setDescriptionCreate('');
    setDescriptionUpdate('');


  };

  return (
    <div>
      <h1>Books</h1>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <ul>
            {books.map((book) => (
              <li key={book.id}>
                {book.title} ({book.author}) {book.description}
                <button onClick={() => handleDelete(book.id)}>Delete</button>
                <button onClick={() => handleView(book.id)}>View</button>

              </li>
            ))}
          </ul>
          {selectedBook && (
            <div>
              <h2>{selectedBook.title}</h2>
              <form onSubmit={handleUpdate}>
                <label>
                  Title:
                  <input type="text" value={title} onChange={(event) => setTitleUpdate(event.target.value)} />
                </label>
                <label>
                  Description:
                  <input type="text" value={description} onChange={(event) => setDescriptionUpdate(event.target.value)} />
                </label>
                <button type="submit">Update</button>
                <button type="button" onClick={handleCancel}>Cancel</button>
              </form>
            </div>
          )}
          <div>
            <h2>Create a new book</h2>
            <form onSubmit={handleCreate}>
              <label>
                Title:
                <input type="text" value={title} onChange={(event) => setTitleCreate(event.target.value)} />
              </label>
              <label>
                Description:
                <input type="text" value={description} onChange={(event) => setDescriptionCreate(event.target.value)} />
              </label>
              <button type="submit">Create</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default BookPage;
