import React, { useState, useEffect } from 'react';
import axios from 'axios';

function BookPage() {
  const [books, setBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    async function fetchBooks() {
      try {
        const response = await axios.get('http://localhost:8000/books');
        setBooks(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    }
    fetchBooks();
  }, []);

  const handleDelete = async (bookId) => {
    try {
      await axios.delete(`/books/${bookId}`);
      setBooks((prevBooks) => prevBooks.filter((book) => book.id !== bookId));
    } catch (error) {
      console.error(error);
    }
  };

  const handleView = async (bookId) => {
    // Implement view book functionality
  };

  const handleUpdate = async (bookId) => {


    // form to update book
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [description, setDescription] = useState('');


    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.put('http://localhost:8000/books', { title, author, description});
            console.log(response.data);
            localStorage.setItem('access', response.data.access);
            localStorage.setItem('refresh', response.data.refresh);
            navigate('/options');

        } catch (error) {

            console.error(error);

        }
        }



  };

  return (
    <div>
      <h1>Books</h1>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <ul>
          {books.map((book) => (
            <li key={book.id}>
              {book.title} ({book.author})
              <button onClick={() => handleDelete(book.id)}>Delete</button>
              <button onClick={() => handleView(book.id)}>View</button>
              <button onClick={() => handleUpdate(book.id)}>Update</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default BookPage;
