import React from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Container from '@material-ui/core/Container';
import Header from './components/Header';
import Footer from './components/Footer';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import SignupPage from './pages/SignupPage';
import { BookCreatePage, BookEditPage } from './pages/AuthorPages';
import AuthorDashboard from './components/AuthorDashboard';
import PrivateRoute from './components/PrivateRoute';
import { useAuth } from './contexts/AuthContext';

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',
  },
  content: {
    flexGrow: 1,
    paddingTop: theme.spacing(3),
    paddingBottom: theme.spacing(3),
  },
}));

function App() {
  const classes = useStyles();
  const { currentUser } = useAuth();

  return (
    <div className={classes.root}>
      <CssBaseline />
      <Router>
        <Header />
        <Container maxWidth="lg" className={classes.content}>
          <Switch>
            <Route exact path="/">
              <HomePage />
            </Route>
            <Route path="/login">
              <LoginPage />
            </Route>
            <Route path="/signup">
              <SignupPage />
            </Route>
            <PrivateRoute path="/author" isAuthenticated={currentUser}>
              <Switch>
                <Route exact path="/author">
                  <AuthorDashboard />
                </Route>
                <Route exact path="/author/books/create">
                  <BookCreatePage />
                </Route>
                <Route exact path="/author/books/:id/edit" component={BookEditPage} />
              </Switch>
            </PrivateRoute>
            <Route path="*">
              <Redirect to="/" />
            </Route>
          </Switch>
        </Container>
        <Footer />
      </Router>
    </div>
  );
}

export default App;
