import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const PagesPage = () => {
  const { id } = useParams();
  const [page, setPage] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [number, setNumber] = useState(null);
  const [content, setContent] = useState(null);

  const fetchPage = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/pages/`);
      setPage(response.data);
      setIsLoading(false);
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  };

  const handleEdit = () => {
    setIsEditing(true);
    setNumber(page.number);
    setContent(page.content);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setNumber(null);
    setContent(null);
  };

  const handleSave = async (PageID) => {
    try {
      const response = await axios.patch(`http://localhost:8000/pages/${id}/`, {
        number,
        content,
      });
      setPage(response.data);
      setIsEditing(false);
    } catch (error) {
      console.error(error);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`http://localhost:8000/pages/${id}/`);
      // Redirect to the book's page after deleting the current page
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {isEditing ? (
            <div>
              <h2>Edit Page {page.number}</h2>
              <form>
                <label>
                  Number:
                  <input
                    type="number"
                    value={number}
                    onChange={(event) => setNumber(event.target.value)}
                  />
                </label>
                <label>
                  Content:
                  <textarea
                    value={content}
                    onChange={(event) => setContent(event.target.value)}
                  />
                </label>
                <button type="button" onClick={handleCancel}>
                  Cancel
                </button>
                <button type="button" onClick={handleSave}>
                  Save
                </button>
              </form>
            </div>
          ) : (
            <div>
              <h2>Page {page.number}</h2>
              <p>{page.content}</p>
              <button onClick={handleEdit}>Edit</button>
              <button onClick={handleDelete}>Delete</button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default PagesPage;
