import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';


function BookPages(props) {
  const { id } = useParams();
  // const id = props.match.params.id;
  const [pages, setPages] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [number, setNumber] = useState('');
  const [content, setContent] = useState('');

  const [isCreating, setIsCreating] = useState(false);
  const [selectedPage, setSelectedPage] = useState(null);

  useEffect(() => {
    async function fetchPages() {
      try {
        const token = localStorage.getItem('access');
        const response = await axios.get(`http://localhost:8000/book/${id}/pages/`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setPages(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    }
    fetchPages();
  }, []);

  const handleDelete = async (pageId) => {
    try {
      const token = localStorage.getItem('access');
      await axios.delete(`http://localhost:8000/pages/${pageId}/`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setPages((prevPages) => prevPages.filter((page) => page.id !== pageId));
    } catch (error) {
      console.error(error);
    }
  };

  const handleView = async (pageId) => {
    try {
      const token = localStorage.getItem('access');
      const response = await axios.get(`http://localhost:8000/pages/${pageId}/`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setSelectedPage(response.data);
      setNumber(response.data.number);
      setContent(response.data.content);
      setBookId(response.data.book_id);

      setIsCreating(false);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCreate = async (event) => {
    event.preventDefault();
    try {
      const token = localStorage.getItem('access');
      const response = await axios.post(
        `http://localhost:8000/books/${props.match.params.id}/pages/`,
        { title, content },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setPages((prevPages) => [...prevPages, response.data]);
      setNumber('');
      setContent('');
      setBookId(id);
      setIsCreating(false);
    } catch (error) {
      console.error(error);
    }
  };

  const handleUpdate = async (event) => {
    event.preventDefault();
    try {
      const token = localStorage.getItem('access');
      const response = await axios.put(
        `http://localhost:8000/pages/${selectedPage.id}/`,
        { title, content },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setPages((prevPages) =>
        prevPages.map((page) => (page.id === selectedPage.id ? response.data : page))
      );
      setSelectedPage(null);
      setNumber('');
      setContent('');
      setBookId(id);
      setIsCreating(false);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Pages</h1>
      <button onClick={() => setIsCreating(true)}>Create new page</button>
      {isCreating && (
        <form onSubmit={selectedPage ? handleUpdate : handleCreate}>
          <div>
            <label htmlFor="title">Number:</label>
            <input
              type="number"
              id="number"
              name="number"
              value={number}
              onChange={(event) => setTitle(event.target.value)}
            />
          </div>
          <div>
            <label htmlFor="content">Content:</label>
            <textarea
              id="content"
              name="content"
              value={content}
              onChange={(event) => setContent(event.target.value)}
            />
          </div>
          <button type="submit">{selectedPage ? 'Update' : 'Create'}</button>
        </form>
      )}
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <ul>
            {pages.map((page) => (
              <li key={page.id}>
                {page.title} ({page.content})
                <button onClick={() => handleDelete(page.id)}>Delete</button>
                <button onClick={() => handleView(page.id)}>Edit</button>
              </li>
            ))}
          </ul>
        </div>
      )}
      {selectedPage && (
        <div>
          <h2>Edit page</h2>
          <form onSubmit={handleUpdate}>
            <div>
              <label htmlFor="title">number:</label>
              <input
                type="number"
                id="number"
                name="number"
                value={title}
                onChange={(event) => setTitle(event.target.value)}
              />
            </div>
            <div>
              <label htmlFor="content">Content:</label>
              <textarea
                id="content"
                name="content"
                value={content}
                onChange={(event) => setContent(event.target.value)}
              />
            </div>
            <button type="submit">Update</button>
            <button onClick={() => setSelectedPage(null)}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default BookPages;
