import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import { Link } from 'react-router-dom';

function handleView (bookId) {
  try {
    const token = localStorage.getItem('access');
    const response = axios.get(`http://localhost:8000/books/${bookId}/`, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    setSelectedBook(response.data);
    setTitle(response.data.title);
    setDescription(response.data.description);
    setIsCreating(false);
  } catch (error) {
    console.error(error);
  }
};


function BookPage() {
  const [books, setBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [selectedBook, setSelectedBook] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [isCreating, setIsCreating] = useState(false);

  useEffect(() => {
    async function fetchBooks() {
      try {
        const response = await axios.get('http://localhost:8000/books/');
        setBooks(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    }
    fetchBooks();
  }, []);

  const handleDelete = async (bookId) => {
    try {
      const token = localStorage.getItem('access');
      await axios.delete(`http://localhost:8000/books/${bookId}/`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setBooks((prevBooks) => prevBooks.filter((book) => book.id !== bookId));
      setSelectedBook(null);
    } catch (error) {
      try{
      const refresh = localStorage.getItem('refresh');
        const response = await axios.post('http://localhost:8000/auth/token/refresh/', { refresh });
        localStorage.setItem('access', response.data.access);
        const token = localStorage.getItem('access');
        await axios.delete(`http://localhost:8000/books/${bookId}/`, {
            headers: {

                Authorization: `Bearer ${token}`,
            },
        });
        setBooks((prevBooks) => prevBooks.filter((book) => book.id !== bookId));
        setSelectedBook(null);
    } catch (error) {
        console.error(error);

    }






    }
  };



  const handleUpdate = async (event) => {
    event.preventDefault();
    try {
      const token = localStorage.getItem('access');
      await axios.put(
        `http://localhost:8000/books/${selectedBook.id}/`,
        { title, description },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const updatedBook = { ...selectedBook, title, description };
      setBooks((prevBooks) =>
        prevBooks.map((book) =>
          book.id === updatedBook.id ? updatedBook : book
        )
      );
      setSelectedBook(updatedBook);
      setIsCreating(false);
    } catch (error) {
        const refresh = localStorage.getItem('refresh');
        const response = await axios.post('http://localhost:8000/auth/token/refresh/', { refresh });
        localStorage.setItem('access', response.data.access);
        const token = localStorage.getItem('access');
        await axios.put(
            `http://localhost:8000/books/${selectedBook.id}/`,
            { title, description },
            {
                headers: {
                    Authorization: `Bearer ${token}`,
                },


            });
        const updatedBook = { ...selectedBook, title, description };
        setBooks((prevBooks) =>
            prevBooks.map((book) =>
                book.id === updatedBook.id ? updatedBook : book
            )
        );
        setSelectedBook(updatedBook);
        setIsCreating(false);


    }
  };

  const handleCreate = async (event) => {
    event.preventDefault();
    try {
      const token = localStorage.getItem('access');
      const response = await axios.post(
        'http://localhost:8000/books/',
        { title, description },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      const newBook = response.data;
      setBooks((prevBooks) => [...prevBooks, newBook]);
      setSelectedBook(newBook);
      setIsCreating(false);
    } catch (error) {
        const refresh = localStorage.getItem('refresh');
        const response = await axios.post('http://localhost:8000/auth/token/refresh/', { refresh });
        localStorage.setItem('access', response.data.access);
        const token = localStorage.getItem('access');
        const response2 = await axios.post(
            'http://localhost:8000/books/',
            { title, description },
            {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            }
        );
        const newBook = response2.data;
        setBooks((prevBooks) => [...prevBooks, newBook]);
        setSelectedBook(newBook);
        setIsCreating(false);
    }
  };

  const handleCancel = () => {
    setSelectedBook(null);
    setTitle('');
    setDescription('');
    setIsCreating(false);
  };

  const handleNewBook = () => {
    setSelectedBook(null);
    setTitle('');
    setDescription('');
    setIsCreating(true);
  };

  return (
    <div>
      <h1>Books</h1>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <ul>
            {books.map((book) => (
              <li key={book.id}>
                {book.title} ({book.description})
                <button onClick={() => handleDelete(book.id)}>Delete</button>
                <button onClick={() => handleView(book.id)}>View</button>
                {/* <button onClick={() => handleUpdate(book.id)}>Update</button> */}

              </li>
            ))}
          </ul>
          {selectedBook && !isCreating && (
            <div>
              <h2>{selectedBook.title}</h2>
              <form onSubmit={handleUpdate}>
                <label>
                  Title:
                  <input
                    type="text"
                    value={title}
                    onChange={(event) => setTitle(event.target.value)}
                  />
                </label>
                <label>
                  Description:
                  <input
                    type="text"
                    value={description}
                    onChange={(event) =>
                      setDescription(event.target.value)
                    }
                  />
                </label>
                <button type="submit">Update</button>
                <button type="button" onClick={handleCancel}>
                  Cancel
                </button>
              </form>
            </div>
          )}
          {!selectedBook && !isCreating && (
            <div>
             <button onClick={handleNewBook}>Create a new book</button>
            </div>
          )}
          {(selectedBook || isCreating) && (
            <div>
              <h2>{isCreating ? 'Create a new book' : selectedBook.title}</h2>
              <form onSubmit={isCreating ? handleCreate : handleUpdate}>
                <label>
                  Title:
                  <input
                    type="text"
                    value={title}
                    onChange={(event) => setTitle(event.target.value)}
                  />
                </label>
                <label>
                  Description:
                  <input
                    type="text"
                    value={description}
                    onChange={(event) =>
                      setDescription(event.target.value)
                    }
                  />
                </label>
                <button type="submit">
                  {isCreating ? 'Create' : 'Update'}
                </button>
                <button type="button" onClick={handleCancel}>
                  Cancel
                </button>
              </form>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default BookPage;
