import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import LoginPage from './pages/token';
import BookPage from './pages/options';
import BookPages from './pages/pages';
import LogoutButton from './pages/logout';
import RegistrationForm from './pages/register';





function App() {

  const [showForm, setShowForm] = useState(false);

  const handleShowForm = () => {
    setShowForm(true);
  };

  return (








    <>

      <div className="App">
        <header className="App-header">
          <h1>Book App</h1>
        </header>
        <main>

        <div>
      <h1>Welcome to My App</h1>
        <LogoutButton />
      </div>
      <div>
      <h1>Registration</h1>
      {showForm ? (
        <RegistrationForm />
      ) : (
        <button onClick={handleShowForm}>Register</button>
      )}
    </div>
        </main>

      </div>
      <Router>
        <Routes>
          <Route path="/login" element={<LoginPage />} />
        </Routes>
        <Routes>
          <Route path="/options" element={<BookPage />} />
        </Routes>

        <Routes>
          <Route path="/book/:id/pages" element={<BookPages />} />
        </Routes>

      </Router></>





  );
}

export default App;
