import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route, Redirect } from 'react-router-dom';
import { Container, Row, Col, Button, Form, Nav, Navbar, NavDropdown } from 'react-bootstrap';
import { login, logout, register, getBooks, getBook, createBook, updateBook, deleteBook } from './services';

function App() {
  const [user, setUser] = useState(null);
  const [books, setBooks] = useState([]);
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      const user = jwtDecode(token);
      setUser(user);
    }
  }, []);

  const handleLogin = async (event) => {
    event.preventDefault();
    const { username, password } = event.target.elements;
    try {
      const user = await login(username.value, password.value);
      setUser(user);
    } catch (error) {
      setError(error.response.data);
    }
  };

  const handleLogout = () => {
    logout();
    setUser(null);
  };

  const handleRegister = async (event) => {
    event.preventDefault();
    const { username, password } = event.target.elements;
    try {
      const user = await register(username.value, password.value);
      setUser(user);
    } catch (error) {
      setError(error.response.data);
    }
  };

  const handleCreateBook = async (event) => {
    event.preventDefault();
    const { title, author, description } = event.target.elements;
    const data = {
      title: title.value,
      author: author.value,
      description: description.value,
    };
    setLoading(true);
    try {
      const book = await createBook(data);
      setBooks([...books, book]);
      setLoading(false);
    } catch (error) {
      setError(error.response.data);
      setLoading(false);
    }
  };

  const handleUpdateBook = async (event) => {
    event.preventDefault();
    const { title, author, description } = event.target.elements;
    const data = {
      title: title.value,
      author: author.value,
      description: description.value,
    };
    setLoading(true);
    try {
      const updatedBook = await updateBook(book.id, data);
      const index = books.findIndex((b) => b.id === book.id);
      const updatedBooks = [...books];
      updatedBooks[index] = updatedBook;
      setBooks(updatedBooks);
      setBook(null);
      setLoading(false);
    } catch (error) {
      setError(error.response.data);
      setLoading(false);
    }
  };

  const handleDeleteBook = async (id) => {
    setLoading(true);
    try {
      await deleteBook(id);
      const updatedBooks = books.filter((b) => b.id !== id);
      setBooks(updatedBooks);
      setLoading(false);
    } catch (error) {
      setError(error.response.data);
      setLoading(false);
    }
  };

  const handleSelectBook = async (id) => {
    setLoading(true);
    try {
      const selectedBook = await getBook(id);
      setBook(selectedBook);
      setLoading(false);
    } catch (error) {
      setError(error.response.data);
      setLoading(false);
    }
  };

  useEffect(() => {
    setLoading(true);
    getBooks()
      .then((books) => setBooks(books))
      .catch((error) => setError(error.response.data))
      .finally(() => setLoading(false));
  }, []);

  return (
    <Router>
      <Navbar bg="light" expand="lg">
        <Navbar.Brand href="/">Book Author API</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="/">Home</Nav.Link>
            {user ? (
              <>
                <NavDropdown title="Books" id="basic-nav-dropdown">
                  <NavDropdown.Item href="/books">All Books</NavDropdown.Item>
                  <NavDropdown.Item href="/books/create">Create Book</NavDropdown.Item>
                </NavDropdown>
                <Nav.Link onClick={handleLogout}>Logout</Nav.Link>
              </>
            ) : (
              <>
                <Nav.Link href="/login">Login</Nav.Link>
                <Nav.Link href="/register">Register</Nav.Link>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Navbar>
      {error && <p>{error}</p>}
      <Switch>
        <Route exact path="/">
          <Container>
            <Row>
              <Col>
                <h1>Welcome to the Book Author API</h1>
                <p>
                  This is a simple React application that consumes the Book Author API. You can browse books, view
                  their pages, and manage your own books and pages if you are logged in.
                </p>
              </Col>
            </Row>
          </Container>
        </Route>
        <Route exact path="/login">
          {user ? (
            <Redirect to="/" />
          ) : (
            <Container>
              <Row>
                <Col>
                  <h1>Login</h1>
                  <Form onSubmit={handleLogin}>
                    <Form.Group controlId="formBasicEmail">
                      <Form.Label>Username</Form.Label>
                      <Form.Control type="text" name="username" placeholder="Enter username" required />
                    </Form.Group>
                    <Form.Group controlId="formBasicPassword">
                      <Form.Label>Password</Form.Label>
                      <Form.Control type="password" name="password" placeholder="Password" required />
                    </Form.Group>
                    <Button variant="primary" type="submit">
                      Login
                    </Button>
                  </Form>
                </Col>
              </Row>
            </Container>
          )}
        </Route>
        <Route exact path="/register">
          {user ? (
            <Redirect to="/" />
          ) : (
            <Container>
              <Row>
                <Col>
                  <h1>Register</h1>
                  <Form onSubmit={handleRegister}>
                    <Form.Group controlId="formBasicEmail">
                      <Form.Label>Username</Form.Label>
                      <Form.Control type="text" name="username" placeholder="Enter username" required />
                    </Form.Group>
                    <Form.Group controlId="formBasicPassword">
                      <Form.Label>Password</Form.Label>
                      <Form.Control type="password" name="password" placeholder="Password" required />
                    </Form.Group>
                    <Button variant="primary" type="submit">
                      Register
                    </Button>
                    </Form> 
                    </Col> 
                    </Row> 
                    </Container> )}
                     </Route> 
                     <Route exact path="/books"> 
                     <Container> 
                     <Row> 
                     <Col> 
                     <h1>Books</h1> 
                     {loading ? ( <p>Loading books...</p> ) : ( <ul> {books.map((book) => ( <li key={book.id}> <a href={`/books/${book.id}`}>{book.title}</a> by {book.author} {user && user.id === book.author_id && ( <> {' '} <Button variant="link" onClick={() => handleSelectBook(book.id)}> Edit </Button>{' '} <Button variant="link" onClick={() => handleDeleteBook(book.id)}> Delete </Button> </> )} </li> ))} </ul> )} </Col> </Row> </Container> </Route> <Route exact path="/books/create"> {user ? ( <Container> <Row> <Col> <h1>Create Book</h1> <Form onSubmit={handleCreateBook}> <Form.Group controlId="formBasicEmail"> <Form.Label>Title</Form.Label> <Form.Control type="text" name="title" placeholder="Enter book title" required /> </Form.Group> <Form.Group controlId="formBasicPassword"> <Form.Label>Author</Form.Label> <Form.Control type="text" name="author" placeholder="Enter book author" required /> </Form.Group> <Form.Group controlId="formBasicPassword"> <Form.Label>Description</Form.Label> <Form.Control as="textarea" rows={3} name="description" placeholder="Enter book description" required /> </Form.Group> <Button variant="primary" type="submit" disabled={loading}> {loading ? 'Creating...' : 'Create'} </Button> </Form> </Col> </Row> </Container> ) : ( <Redirect to="/" /> )} </Route> <Route exact path="/books/:id"> <Container> <Row> <Col> {loading ? ( <p>Loading book...</p> ) : book ? ( <> <h1>{book.title}</h1> <p>Author: {book.author}</p> <p>Description: {book.description}</p> <h2>Pages</h2> <ul> {book.pages.map((page) => ( <li key={page.id}> <a href={`/pages/${page.id}`}>{page.title}</a> </li> ))} </ul> {user && user.id === book.author_id && ( <> <Button variant="link" onClick={() => handleSelectBook(book.id)}> Edit </Button>{' '} <Button variant="link" onClick={() => handleDeleteBook(book.id)}> Delete </Button> </> )} </> ) : ( <p>Book not found.</p> )} </Col> </Row> </Container> </Route> <Route exact path="/books/:id/edit"> {user && book && user.id === book.author_id ? ( <Container> <Row> <Col> <h1>Edit Book</h1> <Form onSubmit={handleUpdateBook}> <Form.Group controlId="formBasicEmail"> <Form.Label>Title</Form.Label> <Form.Control type="text" name="title" placeholder="Enter book title" defaultValue={book.title} required /> </Form.Group> <Form.Group controlId="formBasicPassword"> <Form.Label>Author</Form.Label> <Form.Control type="text" name="author" placeholder="Enter book author" defaultValue={book.author} required /> </Form.Group> <Form.Group controlId="formBasicPassword"> <Form.Label>Description</Form.Label> <Form.Control as="textarea" rows={3} name="description" placeholder="Enter book description" defaultValue={book.description} required /> </Form.Group> <Button variant="primary" type="submit" disabled={loading}> {loading ? 'Updating...' : 'Update'} </Button> </Form> </Col> </Row> </Container> ) : ( <Redirect to="/" /> )} </Route> <Route exact path="/pages/:id"> <Container> <Row> <Col> <h1>Page</h1> </Col> </Row> </Container> </Route> </Switch> </Router> ); }

export default App;
