function BookPages(props) {
  const [pages, setPages] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [isCreating, setIsCreating] = useState(false);

  useEffect(() => {
    async function fetchPages() {
      try {
        const response = await axios.get(`http://localhost:8000/books/${props.match.params.id}/pages/`);
        setPages(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error(error);
        setIsLoading(false);
      }
    }
    fetchPages();
  }, [props.match.params.id]);

  const handleDelete = async (pageId) => {
    try {
      const token = localStorage.getItem('access');
      await axios.delete(`http://localhost:8000/pages/${pageId}/`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setPages((prevPages) => prevPages.filter((page) => page.id !== pageId));
    } catch (error) {
      console.error(error);
    }
  };

  const handleCreate = async (event) => {
    event.preventDefault();
    try {
      const token = localStorage.getItem('access');
      const response = await axios.post(
        `http://localhost:8000/books/${props.match.params.id}/pages/`,
        { title, content },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setPages((prevPages) => [...prevPages, response.data]);
      setTitle('');
      setContent('');
      setIsCreating(false);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Pages</h1>
      <button onClick={() => setIsCreating(true)}>Create new page</button>
      {isCreating && (
        <form onSubmit={handleCreate}>
          <div>
            <label htmlFor="title">Title:</label>
            <input
              type="text"
              id="title"
              name="title"
              value={title}
              onChange={(event) => setTitle(event.target.value)}
            />
          </div>
          <div>
            <label htmlFor="content">Content:</label>
            <textarea
              id="content"
              name="content"
              value={content}
              onChange={(event) => setContent(event.target.value)}
            />
          </div>
          <button type="submit">Create</button>
        </form>
      )}
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <ul>
            {pages.map((page) => (
              <li key={page.id}>
                {page.title} ({page.content})
                <button onClick={() => handleDelete(page.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
